import pytest
from req import PetFriends

pf = PetFriends()

def test_get_api_for_valid_user(email="vasya@mail.com", password="12345"):
    status, result = pf.get_api_key(email, password)
    assert status == 200
    assert 'key' in result

def test_get_api_for_pets(auth_key="4525bffbdaa621d81ee475f0caf62ec75d7015affab16921d1b99a56", filter="my_pets"):
    status, result = pf.get_api_pets(auth_key, filter)
    assert status == 200

def test_post_api_create_pet_simple(auth_key="4525bffbdaa621d81ee475f0caf62ec75d7015affab16921d1b99a56", name="bob", age="2", animal_type="german"):
    status, result = pf.post_api_create_pet_simple(auth_key, name, age, animal_type)
    assert status == 200

def test_post_api_create_pets(auth_key="4525bffbdaa621d81ee475f0caf62ec75d7015affab16921d1b99a56", name="bob", age="2", animal_type="german", pet_photo="data:image/jpg;base64,"):
    status, result = pf.post_api_create_pets(auth_key, name, age, animal_type, pet_photo)
    assert status == 200